import React,{useContext} from 'react'
import {GlobalState} from '../../../GlobalState'
import { useParams, Link } from 'react-router-dom'

var data = 0
document.getElementById("quantity").innerText = data;
function decrement(data) {

    data=data-1;

}
function Cart() {
    const state = useContext(GlobalState)
    const [cart] = state.userAPI.cart
    
    if (cart.length === 0)
        return <h2 style={{textAlign:"center",fontSize:"3rem"}}>Cart Empty</h2>
        
    const quantityControl = (qty) =>{
        return(
            <>
            <div class="input-group plus-minus-input">
            <div class="input-group-button">
                <button type="button" class="button hollow circle" data-quantity="minus" data-field="quantity">
                <i class="fa fa-minus" aria-hidden="true"></i>
                </button>
            </div>
            <input class="input-group-field" type="number" id="quantity" value="0"/>
            <div class="input-group-button">
                <button type="button" class="button hollow circle" data-quantity="plus" data-field="quantity">
                <i class="fa fa-plus" aria-hidden="true"></i>
                </button>
            </div>
            </div>
            </>
        )
    }
    return (
        <>
        <table className="customers">
          <tr>
            <th>Product</th>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
          </tr>
            {
                cart.map(product =>{
                    return(
                        <tr>
                          <td><img src={product.images.url} alt=''/></td><td>{product.title}</td>
                          <td>{product.price}</td>
                            <td>
                                <div class="input-group">
                                <input type="button" value="-" class="button-minus" data-field="quantity"/>
                                <input type="number" step="1" max="" value="1" name="quantity" class="quantity-field"/>
                                <input type="button" value="+" class="button-plus" data-field="quantity"/>
                                </div>
                            </td>

                          <td>{product.price*product.quantity}</td>
                        </tr>
                    )
                })
            }
            </table>
        </>
    )
}
export default Cart
