import React,{useContext,useState,useEffect} from 'react'
import { GlobalState } from "../../../GlobalState";
import { useParams, Link } from 'react-router-dom'

export default function HistoryID() {
    const state = useContext(GlobalState);
    const params = useParams()
    const [orders,setOrders] =state.userAPI.orders
    const [history,setHistory] = useState([])
    
    useEffect(() => {
        if(params.id){
            orders.forEach(order => {
                if(order._id === params.id) setHistory(order.user_information.cart)
            })
        }
    },[params.id,orders])
    console.log(history)
    return (
        <div>
            <table className="customers">
            <tr>
                <th>Order ID</th>
                <th>Buying Date</th>
                <th>Payment Method</th>
                <th>Delivery Time</th>
                <th>Ship Fee</th>
                <th id="total">Total Price</th>
                
            </tr>
            {order.map((cart) => {
                return (
                <tr>
                    
                    <td>
                    <Link to={"/account/history/"+cart._id}>
                        {cart._id}
                    </Link>
                    </td>
                    <td>{cart.createdAt}</td>
                    <td>{cart.payment}</td>
                    <td>{cart.delivery_time}</td>
                    <td>{cart.ship_fee}</td>
                    <td>{cart.total}</td>
                </tr>
                );
            })}
            </table>
        </div>
    )
}
