import React, { useContext, useState, useEffect } from "react";
import { GlobalState } from "../../../GlobalState";
import { useParams, Link } from "react-router-dom";
import ReactDOM from "react-dom";

function Cart() {
  const state = useContext(GlobalState);
  const [cart] = state.userAPI.cart;
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const calcTotal = () => {
      const total = cart.reduce((prev,item) => {
          return prev + (item.price * item.quality)
      },0)
      setTotal(total)
    }
    calcTotal()
  }, [])


  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "3rem" }}>Cart Empty</h2>
    );

  const quantityControl = (qty) => {
    return (
      <>
        <div class="input-group">
          <input
            type="button"
            value="-"
            class="button-minus"
            data-field="quantity"
          />
          <input
            type="number"
            step="1"
            max=""
            value="1"
            name="quantity"
            class="quantity-field"
          />
          <input
            type="button"
            value="+"
            class="button-plus"
            data-field="quantity"
          />
        </div>
      </>
    );
  };
  return (
    <>
    <div>
      <table className="customers">
          <tr>
            <th>Product</th>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th id="total">Total</th>
          </tr>
          {cart.map((product) => {
            return (
              <tr>
                <td>
                  <img src={product.images.url} alt="" />
                </td>
                <td>{product.title}</td>
                <td>{product.price}</td>
                <td>
                  <div class="input-group">
                    <input
                      type="button"
                      value="-"
                      class="button-minus"
                      data-field="quantity"
                    />
                    <input
                      type="number"
                      step="1"
                      max=""
                      value="1"
                      name="quantity"
                      class="quantity-field"
                    />
                    <input
                      type="button"
                      value="+"
                      class="button-plus"
                      data-field="quantity"
                    />
                  </div>
                </td>

                <td>${total}</td>
              </tr>
            );
          })}
      </table>
        <p className='total-price'>Total price: ${total}</p>
        <Link to="/">
          <input type="submit" value="Continue to checkout" class="checkout" />
        </Link>
    </div>
    </>
  );
}
export default Cart;
