import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
export default function Login() {
    
    const [user,setUser] = useState("")
    const [password,setPassword] = useState("")

    const setUserPassword = e => {
        const {name,value} = e.target;
        setUser({...user,[name]:value})
    }

    const login = async e => {
        e.preventDefault()
        try{
            await axios.post('/user/login',{...user})
            localStorage.setItem('firstLogin',true)
            window.location.href = "/";
        } catch(err){
            alert(err.response.data.msg)
        }
    }
    return (
        <div className='login-page'>
            <form onSubmit={async(e)=>{
                e.preventDefault()
                try{
                    await axios.post('/user/login',{...user})
                    localStorage.setItem('firstLogin',true)
                    window.location.href = "/";
                } catch(err){
                    alert(err.response.data.msg)
                }
            }}>
                <input type="text" name="email" required
                 placeholder='Email address' value={user.email} onChange={(e)=>{setUser(e.target.value)}} />
                <input type="text" name="password" required autoComplete='on'
                 placeholder='Password' value={user.password} onChange={(e)=>{setUser(e.target.value)}}/>
                <div className='row'>
                    <button type='submit'>Login</button>
                    <Link to="/register">Register</Link>
                </div>
            </form>
        </div>
    )
}
