import React,{useContext,useState, useEffect} from 'react'
import axios from 'axios'
import { GlobalState } from "../../../GlobalState";
import { render } from 'react-dom';

export default function Payment() {
    const state = useContext(GlobalState);
    const [infor] = state.userAPI.infor
    const [cart, setCart] = state.userAPI.cart;
    const [total, setTotal] = useState(0);

    
    const [shipFee,setShipFee] = useState('');

    const [totalCash,setTotalCash] = useState('');

    const setShipTheFee = (e) => {
        console.log(e.target.value)
    };
      
    const checkMulti = () => {
        if (cart.length > 1){
            return cart.length+" products"
        }else return cart.length+" product"
    };
    useEffect(() => {
        
        const calcTotal = () => {
          const total = cart.reduce((prev,item) => {
              return prev + (item.price * item.quantity)
          },0)
          setTotal(total)
        }
        calcTotal()
      }, [cart])
    render()
    {
    return (
        
        <div className='checkout-payment'>
            <div className='cart-payment'>
                <div className='ship-method'>
                    <h3>1. Choose shipping method</h3>
                    <div className='ship-method-detail'>
                            <label className='delivery'>
                            Fast Delivery - $10
                                <input type='radio' checked name='delivery' value='10' onChange={setShipTheFee}/>
                                <span class="checkmark"></span>
                            </label>
                            <label className='delivery'>
                            Economical Delivery - $5
                                <input type='radio' checked name='delivery'value='15'onChange={setShipTheFee}/>
                                <span class="checkmark"></span>
                            </label>
                    </div>
                </div>
                <div className='payment-method'>
                <h3>2. Choose payment method</h3>
                    <div className='payment-method-detail'>
                        <label className='delivery'>
                            Direct Payment (Face-to-face payment to the shipper)
                                <input type='radio' checked name='payment' value='direct'/>
                                <span class="checkmark"></span>
                            </label>
                            <label className='delivery'>
                            Paypal Payment
                                <input type='radio' checked name='payment'value='paypal'/>
                                <span class="checkmark"></span>
                        </label>
                    </div>
                </div>
            </div>
            <div className='user-payment'>
                <h3>Payment Information</h3>
                <div className='user-payment-detail'>
                    <h4>Delivery Address</h4>
                    <p className='name'>{infor.name}</p>
                    
                    <h4>Your cart</h4>
                    <span>{checkMulti()} </span>
                    <span className='cart-detail-dropdown'>Details</span>
                    <div className='payment-cart-summary'>
                        {
                            cart.map((item) => {
                                return(
                                    <div className='item-detail'>

                                        <p className='number'>{item.quantity}x</p>
                                        <p className='name'>{item.title}</p>
                                        <p className='price'>${item.price}</p>
                                    </div>
                                )
                            })
                        }
                    
                    </div>
                    <hr></hr>
                    <div className='total-cash'>
                        <div className='temp-cash'>
                            <div>Provisional</div>
                            <div>${total}</div>
                        </div>
                        <div className='vat'>
                            <span>VAT</span>
                            <span>${total * 10/100}</span>
                        </div>
                        <div className='ship-fee'>
                            <span>Shipping Fee</span>
                            <span>$10</span>
                        </div>
                        <div className='total-payment'>
                            <h4>Total Cash: </h4>
                            <span>{shipFee}</span>
                        </div>
                    </div>
                    
                </div>
            </div>
        </div>
    )};
}
