import React,{useContext,useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import trash_can from './iconmonstr-trash-can-1.svg'
import { useParams, Link } from "react-router-dom";
export default function UserManagment() {
    const state = useContext(GlobalState)
    const [users] = state.userAPI.users
    const adminShift = (id) => {
      users.forEach((user,index) => {
        if(user.role === id){
          users.splice(index,1)
        }
      })
    }
    adminShift(1)
    const displayRole = (role) => {
      if (role === 0){
        return "User"
      }else if (role === 1){
        return "Admin"
      }else if (role === 2){
        return "Seller"
      }else return "Unknown"
    }

    return (
        <div className='users'>
          <Link to="/product-management">
            <button class="button-3" role="button">Product Management</button>
          </Link>
            <table className="customers">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Role</th>
            <th><img src={trash_can} alt="" className='trash-can'/></th>
          </tr>
          {users.map((user) => {
            return (
              <tr >
                <td>{user.name}</td>
                <td className='user-email'>{user.email}</td>
                <td>{user.address}</td>
                <td>{user.phone}</td>
                <td>
                <div class="select" tabindex="1">
                  <input class="selectopt" name="test" type="radio" id="opt1" checked="checked"/>
                  <label for="opt1" class="option">Oranges</label>
                  <input class="selectopt" name="test" type="radio" id="opt2"/>
                  <label for="opt2" class="option">Apples</label>
                  <input class="selectopt" name="test" type="radio" id="opt3"/>
                  <label for="opt3" class="option">Grapefruit</label>
                  <input class="selectopt" name="test" type="radio" id="opt4"/>
                  <label for="opt4" class="option">Bananas</label>
                  <input class="selectopt" name="test" type="radio" id="opt5"/>
                  <label for="opt5" class="option">Watermelon</label>
                </div>
                </td>
                <td><img src={trash_can} alt="" className='trash-can'/></td>
              </tr>
            )})}
            </table>
        </div>
    )
}
