import React, { useContext } from "react";
import { GlobalState } from "../../../GlobalState";

export default function Filters() {
  const state = useContext(GlobalState);

  const [category, setCategory] = state.productsAPI.category;
  const [sort, setSort] = state.productsAPI.sort;
  const [search, setSearch] = state.productsAPI.search;

  const handleCategory = (e) => {
    var value = "";
    if (e.target.value === "") {
      value = "";
    } else value = "category=" + e.target.value;

    setCategory(value);
  };

  return (
    <div className="filter_menu">
      
        <span className="title">Filters: </span>
      <div className="selection_column">
        <select name="category" onChange={handleCategory} className="row">
          <option value="" className="choice">
            All Products
          </option>
          <option value="laptop" className="choice">
            Laptop
          </option>
          <option value="mobile phone" className="choice">
            Mobile Phone
          </option>
          <option value="other" className="choice">
            Other
          </option>
        </select>
      </div>
      <div className="row">
        <span className="title">Sort By: </span>
      </div>
      <div className="selection_column">
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="" className="choice">
            Newest
          </option>
          <option value="sort=oldest" className="choice">
            Oldest
          </option>
          <option value="sort=-sold" className="choice">
            Best sales
          </option>
          <option value="sort=-price" className="choice">
            Price: Hight-Low
          </option>
          <option value="sort=price" className="choice">
            Price: Low-Hight
          </option>
        </select>
      </div>
    </div>
  );
}
