import React, { useContext, useState, useEffect } from "react";
import { GlobalState } from "../../../GlobalState";
import { useParams, Link } from "react-router-dom";
import ReactDOM from "react-dom";
import trash_can from './iconmonstr-trash-can-1.svg'
import axios from 'axios'

function Cart() {
  const state = useContext(GlobalState);
  const [cart, setCart] = state.userAPI.cart;
  const [token] = state.token
  const [total, setTotal] = useState(0);
  const [order,setOrder] =state.userAPI.orders
  
  console.log(order)
  
  const dateHandle = (time) => {
    const date = new Date(time);
    year = date.getFullYear();
    month = date.getMonth()+1;
    dt = date.getDate();

    if (dt < 10) {
    dt = '0' + dt;
    }
    if (month < 10) {
    month = '0' + month;
    }
  }
    
  if (order.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "3rem" }}>Cart Empty</h2>
    );
  return (
    <>
    <div>
      <table className="customers">
          <tr>
            <th>Order ID</th>
            <th>Buying Date</th>
            <th>Payment Method</th>
            <th>Delivery Time</th>
            <th>Ship Fee</th>
            <th id="total">Total Price</th>
            
          </tr>
          {order.map((cart) => {
            return (
              <tr>
                
                <td>
                <Link to={"/account/history/"+cart._id}>
                    {cart._id}
                </Link>
                </td>
                <td>{cart.createdAt}</td>
                <td>{cart.payment}</td>
                <td>{cart.delivery_time}</td>
                <td>{cart.ship_fee}</td>
                <td>{cart.total}</td>
              </tr>
            );
          })}
      </table>
        
    </div>
    </>
  );
}
export default Cart;
