import React,{useContext,useEffect,useState} from 'react'
import {GlobalState} from '../../../GlobalState'
import trash_can from './iconmonstr-trash-can-1.svg'
import { useParams, Link } from "react-router-dom";
export default function UserManagment() {
    const state = useContext(GlobalState)
    const [users] = state.userAPI.users

    const [user,setUser] = useState([])
    const [seller,setSeller] = useState([])
    const [role,setRole] = useState([])
    const adminShift = (id) => {
      users.forEach((user,index) => {
        if(user.role === id){
          users.splice(index,1)
        }
      })
    }
    adminShift(1)
    const displayRole = (role) => {
      if (role === 0){
        return "User"
      }else if (role === 1){
        return "Admin"
      }else if (role === 2){
        return "Seller"
      }else return "Unknown"
    }
    const handleChange = (e) => {
      let index = e.nativeEvent.target.selectedIndex;
      var name = e.nativeEvent.target[index].text;
      var value = e.target.value
      console.log(name)
      if(name === 'User'){
        setUser([...user,value])
      }
      else{
        setSeller([...seller,value])
      }
      
    }
    const Role = () => {
      console.log(user)
      console.log(seller)
      setRole({...role,["user"]:user})
      
      console.log(role)
      setRole({})
    }
    return (
        <div className='users'>
          <Link to="/product-management">
            <button class="button-3" role="button">Product Management</button>
          </Link>
            <table className="customers">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Role</th>
            <th><img src={trash_can} alt="" className='trash-can'/></th>
          </tr>
          {users.map((user) => {
            return (
              <tr >
                <td>{user.name}</td>
                <td className='user-email'>{user.email}</td>
                <td>{user.address}</td>
                <td>{user.phone}</td>
                <td>
                <select className='role' onChange={handleChange}>
                  <option label = 'User' value={user._id} selected={displayRole(user.role)==='User'} >User</option>
                  <option label = 'Seller' value={user._id} selected={displayRole(user.role)==='Seller'}>Seller</option>
                </select>
                </td>
                <td><img src={trash_can} alt="" className='trash-can'/></td>
              </tr>
            )})}
            </table>
            <div>
            <input type="submit" value="Confirm" class="checkout" onClick={Role}/>
            </div>
        </div>
    )
}
